let config = global.get("config");
let containers = msg.containers;

// Convertir array en objeto con clave por ID
let containerMap = {};
for (let c of containers) {
    containerMap[c.id] = c;
}

// Reemplazar containers en msg
msg.containers = containerMap;

// Validaciones
if (!config.id) {
    node.error("Falta el campo 'id'", msg);
    return null;
}

// if (config.action !== 0 && config.action !== 1) {
//     node.error("El campo 'action' debe ser 0 (vaciado) o 1 (llenado)", msg);
//     return null;
// }

// if (typeof config.preset !== "number" || config.preset <= 0) {
//     node.error("El campo 'preset' debe ser un n√∫mero positivo", msg);
//     return null;
// }

if (!msg.containers.hasOwnProperty(config.id)) {
    node.error(`El ID '${config.id}' no existe en containers`, msg);
    return null;
}

// Guardar contenedor coincidente en variable global
global.set("container", msg.containers[config.id]);

msg.validatedConfig = config;

return msg;

const container = global.get("container");
if (
  !container ||
  !container.calibration_table ||
  !container.height ||
  !container.thresholds
) {
  node.error("Datos del recipiente incompletos o no definidos");
  return null;
}

// 1. CONFIGURACI√ìN AJUSTADA
const config = {
  TOLERANCIA_NIVEL_CERO: 0.05,
  TOLERANCIA_CAUDAL: 0.001, // m√°s sensible

  TOLERANCIA_UMBRAL: 0.01,

  INTERVALO_MINIMO: 0.5,
  RETRASO_ESTABILIZACION: 2,

  HISTERESIS: 0.02,
  MIN_MUESTRAS_OPERACION: 1,
  TAMANO_HISTORIAL: 3,

  FACTOR_SUAVIZADO_PRINCIPAL: 0.7,
  FACTOR_SUAVIZADO_SECUNDARIO: 0.3,
  FACTOR_SUAVIZADO_ETA: 0.4,
  FACTOR_SUAVIZADO_NIVEL: 0.3,

  MAX_ETA: 24 * 3600,
  NIVEL_MAX_OVERSHOOT: 0.5,
};

// 2. DATOS B√ÅSICOS
const tabla = container.calibration_table;
const volumenMax = tabla[tabla.length - 1].volume;
const alturaMax = tabla[tabla.length - 1].height;

// 3. UMBRALES
function calcularUmbrales() {
  const thresholds = container.thresholds;
  const h_h_volumen = interpolar(tabla, thresholds.high_high * alturaMax);
  const l_l_volumen = interpolar(tabla, thresholds.low_low * alturaMax);
  const h_h_volumen_tol = h_h_volumen - volumenMax * config.TOLERANCIA_UMBRAL;
  const l_l_volumen_tol = l_l_volumen + volumenMax * config.TOLERANCIA_UMBRAL;

  return {
    h_h: h_h_volumen,
    l_l: l_l_volumen,
    h_h_tol: h_h_volumen_tol,
    l_l_tol: l_l_volumen_tol,
    h_h_nivel: thresholds.high_high * alturaMax,
    l_l_nivel: thresholds.low_low * alturaMax,
  };
}

const umbrales = calcularUmbrales();

// 4. INTERPOLACI√ìN
function interpolar(tabla, h) {
  if (h <= tabla[0].height) return tabla[0].volume;
  if (h >= tabla[tabla.length - 1].height)
    return tabla[tabla.length - 1].volume;

  for (let i = 0; i < tabla.length - 1; i++) {
    if (h >= tabla[i].height && h <= tabla[i + 1].height) {
      const f = (h - tabla[i].height) / (tabla[i + 1].height - tabla[i].height);
      return tabla[i].volume + f * (tabla[i + 1].volume - tabla[i].volume);
    }
  }
  return volumenMax;
}

// 5. ESTADO ANTERIOR
const prev = context.get("prev") || {
  historialNivel: Array(config.TAMANO_HISTORIAL).fill(0),
  historialQ: Array(config.TAMANO_HISTORIAL).fill(0),
  ultimoEstable: { nivel: 0, volumen: 0, ts: 0 },
};

// 6. DATOS ACTUALES
const alturaSensorFondo = container.sensor_height;
const distancia = parseFloat(msg.payload.distance_cm);
const T = parseFloat(msg.payload.temperature);
const ts = Date.now();

// Nivel ajustado
const nivelBruto = Math.max(0, alturaSensorFondo - distancia);
const nivelAjustado =
  nivelBruto <= config.TOLERANCIA_NIVEL_CERO ? 0 : nivelBruto;

// Suavizado del nivel
prev.historialNivel.shift();
prev.historialNivel.push(nivelAjustado);
const nivelFiltrado =
  prev.historialNivel.reduce((s, v) => s + v, 0) / config.TAMANO_HISTORIAL;

// 7. VOLUMEN AJUSTADO
const beta = 0.000214;
const tRef = 20;
const V0 = interpolar(tabla, nivelFiltrado);
let Vadj = V0 * (1 + beta * (T - tRef));
Vadj = Math.max(0, Math.min(Vadj, volumenMax));
if (nivelAjustado === 0) Vadj = 0;

// 8. CAUDAL
const deltaT = Math.max(0.1, (ts - (prev.ts || ts)) / 1000);
const deltaV = Vadj - (prev.V || Vadj);
const Qtemp = deltaV / deltaT;

// node.warn(`ŒîV=${deltaV.toFixed(5)}, ŒîT=${deltaT.toFixed(3)}, Qtemp=${Qtemp.toFixed(5)}`);

const mediaMovil =
  prev.historialQ.reduce((s, v) => s + v, 0) / config.TAMANO_HISTORIAL;
const Q =
  config.FACTOR_SUAVIZADO_PRINCIPAL * Qtemp +
  (1 - config.FACTOR_SUAVIZADO_PRINCIPAL) * mediaMovil;

prev.historialQ.shift();
prev.historialQ.push(Qtemp);

// 9. ESTADO ACTUAL SIMPLIFICADO
let estado;
if (Math.abs(Q) > config.TOLERANCIA_CAUDAL) {
  estado = Q < 0 ? "vaciado" : "llenado";
} else {
  estado = "reposo";
}

// 10. ETA
let ETA = prev.ETA || 0;
if (Math.abs(Q) > config.TOLERANCIA_CAUDAL && isFinite(Q)) {
  if (estado === "llenado") {
    const volumenObjetivo = Math.min(
      umbrales.h_h,
      volumenMax - config.NIVEL_MAX_OVERSHOOT
    );
    const rawETA = Vadj >= volumenObjetivo ? 0 : (volumenObjetivo - Vadj) / Q;
    ETA =
      config.FACTOR_SUAVIZADO_ETA * rawETA +
      (1 - config.FACTOR_SUAVIZADO_ETA) * (prev.ETA || rawETA);
  } else if (estado === "vaciado") {
    const rawETA =
      Vadj <= umbrales.l_l_tol ? 0 : (Vadj - umbrales.l_l) / Math.abs(Q);
    ETA =
      config.FACTOR_SUAVIZADO_ETA * rawETA +
      (1 - config.FACTOR_SUAVIZADO_ETA) * (prev.ETA || rawETA);
  }
  ETA = Math.min(Math.max(0, ETA), config.MAX_ETA);
} else {
  ETA = prev.ETA ? prev.ETA * 0.9 : 0;
}

// 11. NIVEL ESTABLE
if (Math.abs(Q) < config.TOLERANCIA_CAUDAL) {
  if (!prev.tsEstable) prev.tsEstable = ts;
  if (ts - prev.tsEstable >= config.RETRASO_ESTABILIZACION * 1000) {
    prev.ultimoEstable = { nivel: nivelFiltrado, volumen: Vadj, ts: ts };
  }
} else {
  prev.tsEstable = null;
}

// 12. ACTUALIZAR CONTEXTO
prev.ts = ts;
prev.V = Vadj;
prev.nivelAjustado = nivelFiltrado;
prev.estado = estado;
prev.Q = estado !== "reposo" ? Q : 0;
prev.ETA = ETA;

// 13. FORMATOS
function formatValue(value, decimals) {
  if (value === undefined || value === null) return "0.000";
  const lim = Math.pow(10, -decimals);
  return value > -lim && value < lim ? "0.000" : value.toFixed(decimals);
}

function formatValueCaudal(value, decimals) {
  if (value === undefined || value === null) return "0.0000";
  return value.toFixed(decimals); // no fuerza a 0.0000 si es peque√±o
}

// 14. SALIDA
const payload = {
  id: container.id,
  nivel_cm: formatValue(nivelFiltrado, 2),
  temperatura_c: T.toFixed(1),
  volumen_L: formatValue(Vadj, 3),
  caudal_Lps: prev.estado !== "reposo" ? formatValueCaudal(prev.Q, 4) : "0.0000",
  ETA_segundos:
    prev.estado !== "reposo" ? Math.max(0, Math.round(prev.ETA)) : "0",
  estado: prev.estado,
  timestamp: msg.payload.timestamp || new Date(ts).toISOString(),
};

context.set("prev", prev);
msg.payload = payload;
return msg;

// Configuraci√≥n del filtro
const HISTORICO_SIZE = 7;  // Tama√±o del hist√≥rico para mediana
const ALPHA = 0.3;         // Factor de suavizado exponencial (0.1 a 0.5)

// Inicializaci√≥n
const historico = flow.get("historico_distancia") || [];
const last_smoothed = flow.get("last_smoothed") || null;

// Validaci√≥n b√°sica (evita NaN o valores absurdos)
if (typeof msg.payload.distance_cm !== "number" || msg.payload.distance_cm <= 0 || msg.payload.distance_cm > 500) {
    node.warn(`Valor inv√°lido descartado: ${msg.payload.distance_cm}`);
    return null;  // Descarta el mensaje
}

// 1. A√±adir al hist√≥rico y aplicar filtro de mediana
historico.push(msg.payload.distance_cm);
if (historico.length > HISTORICO_SIZE) historico.shift();

const mediana = [...historico].sort((a, b) => a - b)[Math.floor(historico.length / 2)];

// 2. Suavizado exponencial
const smoothed = last_smoothed !== null
    ? ALPHA * mediana + (1 - ALPHA) * last_smoothed
    : mediana;

// Guardar estado para pr√≥ximo mensaje
flow.set("historico_distancia", historico);
flow.set("last_smoothed", smoothed);

// Actualizar el mensaje con el valor filtrado
msg.payload.distance_cm = smoothed;
msg.payload.temperature = msg.payload.temperature;  // Mantener temperatura original

return msg;

let container = global.get("container");
let height = container.sensor_height;

msg.payload = `/home/simonll4/python/.venv/bin/python /home/simonll4/python/data_acquisition.py --height ${height}`;
return msg;

// 1. Obtener configuraci√≥n del contenedor
const container = global.get("container");
if (!container?.thresholds) {
  node.error("No se encontraron umbrales en el container");
  return null;
}

// Obtener el id del contenedor
let id = container.id;

// 2. Calcular umbrales absolutos (solo una vez)
let ctx = context.get("nivelContext") || {};
if (!ctx.umbrales) {
  ctx.umbrales = {
    bajo_bajo: container.height * container.thresholds.low_low,
    bajo: container.height * container.thresholds.low,
    alto: container.height * container.thresholds.high,
    alto_alto: container.height * container.thresholds.high_high,
  };
}

// 3. Obtener y validar medici√≥n actual
let nivel = msg.payload.nivel_cm;

if (typeof nivel !== "number") {
  nivel = parseFloat(nivel);
  if (isNaN(nivel)) {
    node.error("El valor de nivel no es un n√∫mero v√°lido", msg);
    return null;
  }
}

const lastAlarm = ctx.lastAlarm || null;
const um = ctx.umbrales;

// Obtener temperatura actual
let temperatura = msg.payload.temperatura_c;

// Asegurarnos que temperatura es un n√∫mero
if (typeof temperatura !== 'number') {
  temperatura = parseFloat(temperatura);
  if (isNaN(temperatura)) {
    node.error("El valor de temperatura no es un n√∫mero v√°lido", msg);
    return null;
  }
}

// 4. Determinar estado actual
let estadoActual = "Normal";
if (nivel <= um.bajo_bajo) estadoActual = "Bajo-Bajo";
else if (nivel <= um.bajo) estadoActual = "Bajo";
else if (nivel >= um.alto_alto) estadoActual = "Alto-Alto";
else if (nivel >= um.alto) estadoActual = "Alto";

// 5. Determinar direcci√≥n del cambio (carga o descarga)
let tendencia = null;
if (ctx.ultimoNivel !== undefined) {
  if (nivel > ctx.ultimoNivel) tendencia = "carga";
  else if (nivel < ctx.ultimoNivel) tendencia = "descarga";
}

// 6. L√≥gica de notificaci√≥n
let notificar = false;
let mensaje = "";

if (estadoActual !== lastAlarm) {
  // Cambi√≥ de estado
  if (estadoActual === "Normal" && lastAlarm !== null) {
    // Volvi√≥ a normal
    notificar = true;
    mensaje = `ALARMA RESUELTA: ${lastAlarm} -> Normal (${nivel.toFixed(
      2
    )} cm)`;
    ctx.lastAlarm = null;
  } else if (estadoActual !== "Normal") {
    // Est√° en estado de alarma
    if (lastAlarm === null) {
      // Primera alarma
      notificar = true;
      mensaje = `ALARMA ${estadoActual}: Nivel ${nivel.toFixed(2)} cm`;
      ctx.lastAlarm = estadoActual;
    } else if (tendencia === "descarga") {
      // En descarga, siempre reportar cambio a estado m√°s cr√≠tico o menos cr√≠tico
      notificar = true;
      mensaje = `ALARMA ${estadoActual}: Nivel ${nivel.toFixed(2)} cm`;
      ctx.lastAlarm = estadoActual;
    } else if (tendencia === "carga") {
      // En carga, solo reportar si se pasa a un estado m√°s cr√≠tico (de B a BB por ejemplo)
      const niveles = ["Normal", "Bajo", "Bajo-Bajo", "Alto", "Alto-Alto"];
      const severidad = (e) => niveles.indexOf(e);
      if (severidad(estadoActual) > severidad(lastAlarm)) {
        notificar = true;
        mensaje = `ALARMA ${estadoActual}: Nivel ${nivel.toFixed(2)} cm`;
        ctx.lastAlarm = estadoActual;
      } // Si baja severidad en carga, no notificar
    }
  }
}

// 7. Guardar contexto y preparar salida
ctx.ultimoNivel = nivel;
context.set("nivelContext", ctx);

flow.set("alarma_actual",estadoActual)

msg.payload = {
  id: id,
  nivel: nivel,
  estado_actual: estadoActual,
  estado_anterior: lastAlarm,
  requiere_notificacion: notificar,
  mensaje: mensaje,
  umbrales: um, // Para depuraci√≥n
  tendencia: tendencia || "sin cambios",
  temperatura: temperatura,
};

return msg;

// Configuraci√≥n de notificaciones (puede ir en context o flow)
const configNotificaciones = {
    notificarBajo: true,
    notificarBajoBajo: true,
    notificarAlto: true,
    notificarAltoAlto: true,
    notificarRecuperacion: true
};

// Obtener datos del mensaje
const { estado_actual, estado_anterior, requiere_notificacion, mensaje, temperatura, id, nivel } = msg.payload;

// Si no requiere notificaci√≥n, termina el flujo (no env√≠a nada)
if (!requiere_notificacion) {
    return null;
}

// Verificar si debemos notificar seg√∫n configuraci√≥n
let enviarEmail = false;

switch (estado_actual) {
    case "Bajo-Bajo":
        enviarEmail = configNotificaciones.notificarBajoBajo;
        break;
    case "Bajo":
        enviarEmail = configNotificaciones.notificarBajo;
        break;
    case "Alto":
        enviarEmail = configNotificaciones.notificarAlto;
        break;
    case "Alto-Alto":
        enviarEmail = configNotificaciones.notificarAltoAlto;
        break;
    case "Normal":
        enviarEmail = configNotificaciones.notificarRecuperacion;
        break;
}

// Si hay que enviar email, preparar el mensaje
if (enviarEmail) {
    msg.payload = {
        id: id,
        estado_actual: estado_actual,
        nivel: nivel,
        asunto: `Alarma de Nivel: ${estado_actual}`,
        cuerpo: mensaje,
        prioridad: estado_actual.includes("-") ? "alta" : "media",
        temperatura: temperatura
    };
    return msg;
}

// Si no hay que enviar email, no retorna nada (flujo termina aqu√≠)
return null;

// Entrada real del nodo de email
let asunto = msg.payload.asunto || "";
let cuerpo = msg.payload.cuerpo || "";
let prioridad = msg.payload.prioridad || "media"; // Pod√©s ajustar esto desde l√≥gica externa si quer√©s
let temperatura = msg.payload.temperatura || "Desconocida";
let id = msg.payload.id || "Desconocido";
let nivel = msg.payload.nivel || "Desconocido";

// √çconos y t√≠tulos por tipo
let tipo = prioridad === "alta" ? "alta" : prioridad === "media" ? "media" : "baja";
let icono = tipo === "alta" ? "üî•" : tipo === "media" ? "‚ö†Ô∏è" : "‚úÖ";
let tituloTipo = tipo === "alta" ? "ALARMA CR√çTICA" : tipo === "media" ? "ALERTA" : "NOTIFICACI√ìN";
let temperaturaIcono = temperatura > 30 ? "üî•" : temperatura > 20 ? "‚ö†Ô∏è" : "‚úÖ";
let idIcono = id === "Desconocido" ? "üîç" : "üîç";


// Fecha formateada
let fechaHora = new Date().toLocaleString('es-ES');

// Detectar si es resoluci√≥n
let esResuelta = /RESUELT[AO]/i.test(cuerpo);

// Extraer t√≠tulo
let titulo = "SIN T√çTULO";

// Tratar de extraer t√≠tulo desde el asunto
let matchTitulo = asunto.match(/: (.+?)( - |$)/);
if (matchTitulo) {
  titulo = matchTitulo[1].trim();
}

// Buscar nivel dentro del cuerpo
/* let matchNivel = cuerpo.match(/Nivel\s+([\d.,]+)\s?cm/i);
if (matchNivel) {
  nivel = `${matchNivel[1]} cm`;
} else {
  let nivelSolo = cuerpo.match(/^\s*([\d.]+)\s*cm$/m);
  if (nivelSolo) nivel = `${nivelSolo[1]} cm`;
}
 */


// Eleg√≠ color seg√∫n el tipo
let colorPrincipal = tipo === "alta" ? "#d9534f" : tipo === "media" ? "#f0ad4e" : "#5cb85c";

// Plantilla HTML
let html = `
<html>
  <body style="font-family: Arial, sans-serif; margin: 0; padding: 0; background: #f9f9f9;">
    <div style="max-width: 600px; margin: auto; background: white; border: 1px solid #ddd; border-radius: 8px; overflow: hidden;">

      <!-- Encabezado -->
      <div style="background: ${colorPrincipal}; color: white; padding: 20px; text-align: center;">
        <img src="https://cdn-icons-png.flaticon.com/512/9170/9170553.png" alt="Logo" style="height: 100px; margin-bottom: 10px;" />
        <h1 style="margin: 0;">${tituloTipo}: ${titulo.toUpperCase()}</h1>
      </div>

      <!-- Contenido principal -->
      <div style="padding: 20px; color: #333;">
        <p><strong>ü™£ ID Contenedor:</strong> ${idIcono} ${id}</p>
        <p><strong>üìÖ Fecha:</strong> ${fechaHora}</p>
        <p><strong>üìå Estado:</strong> ${esResuelta ? '<span style="color: green;">RESUELTO</span>' : '<span style="color: red;">EN ALERTA</span>'}</p>
        <p><strong>üìè Medici√≥n actual:</strong> ${nivel}</p>
        <p><strong>üå°Ô∏è Temperatura actual:</strong> ${temperaturaIcono} ${temperatura}¬∞C</p>

        <p><strong>üõ† Acciones recomendadas:</strong></p>
        <ul>
          ${esResuelta
    ? "<li>Condici√≥n normal restablecida</li>"
    : tipo === "alta"
      ? "<li>Verificaci√≥n inmediata requerida</li><li>Notificar al personal responsable</li>"
      : "<li>Monitorear la evoluci√≥n</li><li>Registrar en bit√°cora</li>"
  }
        </ul>
      </div>

      <!-- Pie -->
      <div style="background: #f0f0f0; padding: 15px; text-align: center; font-size: 12px; color: #666;">
        Sistema Automatizado de Monitoreo<br>
        Arquitectura Orientada a Servicios - Proyecto Final
      </div>
    </div>
  </body>
</html>
`;

// Asignar al mensaje
msg.topic = `[MONITOR] ${tituloTipo}: ${titulo} - ${esResuelta ? "RESUELTO" : "ALERTA"}`;
msg.payload = html;

return msg;

// Extraer datos del payload
let estado = msg.payload.estado_actual || "";
let nivel = msg.payload.nivel || "Desconocido";
let temperatura = msg.payload.temperatura || "Desconocida";
let id = msg.payload.id || "Desconocido";

// Mapear estado a abreviatura
let estadoAbrev = "";
switch (estado) {
    case "Bajo-Bajo":
        estadoAbrev = "BB";
        break;
    case "Bajo":
        estadoAbrev = "B";
        break;
    case "Alto":
        estadoAbrev = "A";
        break;
    case "Alto-Alto":
        estadoAbrev = "AA";
        break;
    default:
        estadoAbrev = "N"; // N para Normal o desconocido
}

// Formatear mensaje MQTT como JSON
let mensaje = {
    estado: estadoAbrev,
    nivel: nivel,
    temperatura: temperatura,
    id: id
};

msg.payload = JSON.stringify(mensaje);

return msg;

const estado = msg.payload?.estado_actual;
const notificar = msg.payload?.requiere_notificacion;

// Verificamos si es una alarma cr√≠tica y requiere notificaci√≥n
if ((estado === "Alto-Alto" || estado === "Bajo-Bajo") && notificar === true) {
    msg.payload = {
        alarma_critica: true,
        estado: estado,
        nivel: msg.payload.nivel,
        mensaje: msg.payload.mensaje
    };
    return msg; // Enviar se√±al al siguiente nodo
}

// No cumple condiciones: no hacer nada
return null;

const estado = flow.get("alarma_actual")

// Si es AA (Alto-Alto), no enviar pulso
if (estado === "Alto-Alto") {
    return null;
}
return msg;

const estado = flow.get("alarma_actual")

if (estado === "Bajo-Bajo") {
    return null;
}
return msg;
